package br.com.alura.notas;

public class Nota {
	
	private String aluno;
	private double valor;
	
	public Nota(String aluno, double valor) {
		this.aluno =aluno;
		this.valor = valor;
	}

	public String getAluno() {
		// TODO Auto-generated method stub
		return aluno;
	}

	public double getValor() {
		// TODO Auto-generated method stub
		return valor;
	}

}
